/**
 * Build a new platform for the platform toy.
 * @return	Created platform or null if there is no more memory (see errno).
 * @note To release the platform, use toy_unlock_platform().
 */
toy_platform_t *toy_new_platform(void) {
	toy_platform_t *pf;

	/* allocation */
	pf = (toy_platform_t *)calloc(1, sizeof(toy_platform_t));
	if(pf == NULL) {
		errno = ENOMEM;
		return NULL;
	}
	/* the new platform is not locked yet */
	pf->usage = 0;

	/* other init */
	/*pf->entry = pf->sp = pf->argv = pf->envp = pf->aux = pf->argc = 0;*/
	pf->sys_env = calloc(1, sizeof(toy_env_t));
	if(pf->sys_env == NULL) {
		errno = ENOMEM;
		free(pf);
		return NULL;
	}

	/* memory initialization */
	pf->mems.named.M = toy_mem_new();
	if(pf->mems.named.M == NULL) {
		free(pf->sys_env);
		free(pf);
		return NULL;
	}


	/* module initialization */
	TOY_MEM_INIT(pf);
	TOY_GRT_INIT(pf);
	TOY_ERROR_INIT(pf);
	TOY_GEN_INT_INIT(pf);
	TOY_LOADER_INIT(pf);
	TOY_CODE_INIT(pf);


	/* return platform */
	return pf;
}


