/**
 * Ensure that the platform is not released before a matching
 * toy_platform_unlock() is performed.
 * @param platform	Platform to lock.
 */
void toy_lock_platform(toy_platform_t *platform) {
	if (platform == NULL)
		return;

	platform->usage++;
}


/**
 * Unlock the platform. If it the last lock on the platform,
 * the platform is freed.
 * @param platform	Platform to unlock.
 */
void toy_unlock_platform(toy_platform_t *platform) {
	if (platform == NULL)
		return;

	/* unlock */
	if(--platform->usage != 0)
		return;

	/* destroy the modules */
	TOY_MEM_DESTROY(platform);
	TOY_GRT_DESTROY(platform);
	TOY_ERROR_DESTROY(platform);
	TOY_GEN_INT_DESTROY(platform);
	TOY_LOADER_DESTROY(platform);
	TOY_CODE_DESTROY(platform);


	/* free the memories */
	toy_mem_delete(platform->mems.named.M);


	/* free system info */
	free(platform->sys_env);

	/* free the platform */
	free(platform);
	platform = NULL;
}


/**
 * Load the program from the given loader.
 * @param platform		Platform.
 * @param loader		Loader to get program from.
 */
void toy_load(toy_platform_t *platform, toy_loader_t *loader) {
	assert(platform);

	/* load in platform's memory */
	toy_loader_load(loader, platform);

	/* initialize system information */
	platform->entry = toy_loader_start(loader);
	toy_set_brk(platform, toy_brk_init(loader));

	/* !!TODO!! add argc,argv... init */
	/* stack initialization */
	toy_stack_fill_env(loader, platform, platform->sys_env);
}


/**
 * Load the given program in the platform and initialize stack
 * @param platform	Platform to load in.
 * @param path		Path of the file to load.
 * @return			0 for success, -1 for error (in errno).
 */
int toy_load_platform(toy_platform_t *platform, const char *path) {
	toy_loader_t *loader;
	assert(platform);

	/* open the file */
	loader = toy_loader_open(path);
	if(loader == NULL)
		return -1;

	/* load in platform's memory */
	toy_load(platform, loader);

	/* close the file */
	toy_loader_close(loader);

	/* return success */
	return 0;
}



