/**
 * Execute the next instruction in the given simulator.
 * It doesn't check if we reached the last instruction, so it should be done
 * separately using the function toy_is_sim_ended
 * @param	sim	the simulator which we simulate within
 */
void toy_step(toy_sim_t *sim)
{
	toy_inst_t*  inst;
	toy_state_t* state = sim->state;

	/* retrieving next instruction */
    inst = toy_decode(sim->decoder, state->PC);

	/* execute it */

	toy_code_table[inst->ident](state, inst);

#ifndef TOY_INF_DECODE_CACHE
#ifndef TOY_FIXED_DECODE_CACHE
#ifndef TOY_LRU_DECODE_CACHE
    /* finally free it */
	toy_free_inst(inst);
#endif
#endif
#endif

	/* ended ? */
	if(sim->addr_exit == state->PC)
		sim->ended = 1;
}

/**
 * Straightforward execution of the simulated programm.
 * It runs and count the number of executed instructions
 * until the programm reached the last instruction.
 * this is the <bold> fastest </bold> way to simulate a programm
 * @param	sim	the simulator which we simulate within
 * @return number of executed instructions
 * */
uint64_t toy_run_and_count_inst(toy_sim_t *sim)
{
	uint64_t i = 0;
    toy_state_t*   state     = sim->state;
    toy_decoder_t* decoder   = sim->decoder;
    toy_address_t  addr_exit = sim->addr_exit;
	toy_inst_t* inst;
	while(!sim->ended) {
		inst = toy_decode(decoder, state->PC);
		toy_code_table[inst->ident](state, inst);
#ifndef TOY_INF_DECODE_CACHE
#ifndef TOY_FIXED_DECODE_CACHE
#ifndef TOY_LRU_DECODE_CACHE
    /* finally free it */
	toy_free_inst(inst);
#endif
#endif
#endif
		i++;
		if(addr_exit == state->PC)
			sim->ended = 1;
	}
	return i;
}

